import React from 'react';
import './FIFOBreakdownVisual.css';

/**
 * FIFOBreakdownVisual Component
 * 
 * Displays a visual breakdown of how a cryptocurrency sale is fulfilled using FIFO accounting.
 * Shows the "buckets" (purchase lots) that are consumed to fulfill a sale, matching the
 * requirement to display different buy dates and how they are used.
 * 
 * Props:
 * - transaction: The transaction object containing lotsConsumed
 * - currency: The cryptocurrency being sold
 */
const FIFOBreakdownVisual = ({ transaction, currency }) => {
  if (!transaction || !transaction.lotsConsumed || transaction.lotsConsumed.length === 0) {
    return null;
  }

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-ZA', {
      style: 'currency',
      currency: 'ZAR',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(amount);
  };

  const formatCrypto = (amount, decimals = 8) => {
    return parseFloat(amount).toFixed(decimals).replace(/\.?0+$/, '');
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-ZA', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const totalAmount = transaction.amount || 0;
  const totalCostBase = transaction.costBase || 0;
  const totalProceeds = transaction.proceeds || 0;
  const capitalGain = transaction.capitalGain || 0;

  return (
    <div className="fifo-breakdown-visual">
      <div className="fifo-header">
        <h4>ðŸ“Š FIFO Breakdown: How This Sale Was Fulfilled</h4>
        <p className="fifo-explanation">
          You sold <strong>{formatCrypto(totalAmount)} {currency}</strong> today. 
          Your calculator pulls from your purchase history using the <strong>FIFO method</strong> 
          (First In, First Out) â€” the oldest coins you bought are the first ones considered sold.
        </p>
      </div>

      <div className="fifo-scenario">
        <div className="scenario-header">
          <h5>The Scenario:</h5>
        </div>

        <div className="purchase-buckets">
          {transaction.lotsConsumed.map((lot, index) => {
            const lotNumber = index + 1;
            const purchaseDate = formatDate(lot.purchaseDate);
            const amountUsed = formatCrypto(lot.amountConsumed);
            const pricePerUnit = formatCurrency(lot.costPerUnit);
            const totalCost = formatCurrency(lot.costBase);
            
            return (
              <div key={index} className="purchase-bucket">
                <div className="bucket-header">
                  <span className="bucket-number">Bucket #{lotNumber}</span>
                  <span className="bucket-date">{purchaseDate}</span>
                </div>
                <div className="bucket-details">
                  <div className="bucket-row">
                    <span className="bucket-label">Amount Purchased:</span>
                    <span className="bucket-value">{amountUsed} {currency}</span>
                  </div>
                  <div className="bucket-row">
                    <span className="bucket-label">Price Per Unit:</span>
                    <span className="bucket-value">{pricePerUnit}</span>
                  </div>
                  <div className="bucket-row bucket-total">
                    <span className="bucket-label">Total Cost:</span>
                    <span className="bucket-value">{totalCost}</span>
                  </div>
                </div>
                {lot.ageInDays && (
                  <div className="bucket-age">
                    Held for {lot.ageInDays} days
                  </div>
                )}
              </div>
            );
          })}
        </div>

        <div className="sale-summary">
          <h5>Today: You sell {formatCrypto(totalAmount)} {currency}</h5>
        </div>
      </div>

      <div className="fifo-deployment">
        <h5>How Your Calculator Deploys FIFO:</h5>
        <p className="deployment-intro">
          To fulfill this {formatCrypto(totalAmount)} {currency} sale, your calculator has to pull from 
          {transaction.lotsConsumed.length === 1 ? ' this bucket' : ` ${transaction.lotsConsumed.length} different buckets`}:
        </p>

        <div className="deployment-steps">
          {transaction.lotsConsumed.map((lot, index) => {
            const lotNumber = index + 1;
            const amountUsed = formatCrypto(lot.amountConsumed);
            const totalCost = formatCurrency(lot.costBase);
            
            return (
              <div key={index} className="deployment-step">
                <div className="step-icon">
                  <span className="step-number">{lotNumber}</span>
                </div>
                <div className="step-content">
                  <div className="step-title">
                    From Bucket #{lotNumber} ({formatDate(lot.purchaseDate)}):
                  </div>
                  <div className="step-details">
                    Take <strong>{amountUsed} {currency}</strong> with cost base of <strong>{totalCost}</strong>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <div className="deployment-totals">
          <div className="total-row">
            <span className="total-label">Total Amount Sold:</span>
            <span className="total-value">{formatCrypto(totalAmount)} {currency}</span>
          </div>
          <div className="total-row">
            <span className="total-label">Total Cost Base (FIFO):</span>
            <span className="total-value">{formatCurrency(totalCostBase)}</span>
          </div>
          <div className="total-row">
            <span className="total-label">Sale Proceeds:</span>
            <span className="total-value">{formatCurrency(totalProceeds)}</span>
          </div>
          <div className="total-row capital-gain-row">
            <span className="total-label">Capital Gain:</span>
            <span className={`total-value ${capitalGain >= 0 ? 'gain' : 'loss'}`}>
              {formatCurrency(capitalGain)}
            </span>
          </div>
        </div>
      </div>

      <div className="fifo-footer">
        <div className="info-box">
          <strong>ðŸ’¡ Why the reviewer wants to see different dates:</strong>
          <p>
            If you sell {formatCrypto(totalAmount)} {currency} today, but you bought that {currency} in 
            {transaction.lotsConsumed.length === 1 ? ' one transaction' : ` ${transaction.lotsConsumed.length} separate transactions`} over time, 
            your calculator shouldn't just show one "Buy Date." It needs to "peel" the layers of your 
            history to show exactly which purchases are being matched to this sale for tax purposes.
          </p>
        </div>
      </div>
    </div>
  );
};

export default FIFOBreakdownVisual;
