import React from 'react';
import FIFOBreakdownVisual from './FIFOBreakdownVisual';

/**
 * FIFOExplorer Component
 * 
 * This component provides FIFO (First In, First Out) visualization for cryptocurrency transactions.
 * It is integrated into TransactionTableEnhanced.js where FIFO breakdown is shown when users 
 * expand transaction rows to see the lots consumed for each disposal.
 * 
 * The main visualization is handled by FIFOBreakdownVisual component which shows:
 * - Different buy dates (purchase "buckets")
 * - How a sale is fulfilled from those purchases
 * - The "peeling" of transaction layers as described in FIFO accounting
 * 
 * This file can also be used as a standalone component if needed.
 */

const FIFOExplorer = ({ transactions }) => {
  if (!transactions || transactions.length === 0) {
    return null;
  }

  // Filter transactions that have FIFO lots consumed (SELL and TRADE transactions)
  const transactionsWithFIFO = transactions.filter(
    tx => tx.lotsConsumed && tx.lotsConsumed.length > 0
  );

  if (transactionsWithFIFO.length === 0) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <p>No disposal transactions with FIFO breakdown available.</p>
      </div>
    );
  }

  return (
    <div className="fifo-explorer-container">
      {transactionsWithFIFO.map((tx, index) => (
        <FIFOBreakdownVisual 
          key={index}
          transaction={tx}
          currency={tx.currency}
        />
      ))}
    </div>
  );
};

export default FIFOExplorer;
