import React from 'react';
import './RiskSummary.css';

/**
 * RiskSummary Component
 * Displays audit risk summary and flagged transaction statistics
 */
const RiskSummary = ({ auditRisk, flags }) => {
  if (!auditRisk) {
    return null;
  }

  const { overall_risk_level, high_risk_count, medium_risk_count, low_risk_count, total_flags, requires_review } = auditRisk;

  // Determine risk level styling
  const getRiskLevelClass = (level) => {
    switch (level) {
      case 'high':
        return 'risk-high';
      case 'medium':
        return 'risk-medium';
      case 'low':
        return 'risk-low';
      default:
        return 'risk-none';
    }
  };

  const getRiskLevelIcon = (level) => {
    switch (level) {
      case 'high':
        return 'üö®';
      case 'medium':
        return '‚ö†Ô∏è';
      case 'low':
        return '‚úì';
      default:
        return '‚úì';
    }
  };

  const getRiskLevelText = (level) => {
    switch (level) {
      case 'high':
        return 'High Risk - Immediate Review Required';
      case 'medium':
        return 'Medium Risk - Review Recommended';
      case 'low':
        return 'Low Risk - Minor Issues Detected';
      default:
        return 'No Issues Detected';
    }
  };

  return (
    <div className="risk-summary">
      <div className="risk-header">
        <h3>üõ°Ô∏è Audit Risk Assessment</h3>
        <div className={`risk-badge ${getRiskLevelClass(overall_risk_level)}`}>
          <span className="risk-icon">{getRiskLevelIcon(overall_risk_level)}</span>
          <span className="risk-text">{getRiskLevelText(overall_risk_level)}</span>
        </div>
      </div>

      <div className="risk-stats">
        <div className="risk-stat">
          <div className="stat-value">{total_flags || 0}</div>
          <div className="stat-label">Total Flags</div>
        </div>
        <div className="risk-stat risk-high">
          <div className="stat-value">{high_risk_count || 0}</div>
          <div className="stat-label">High Risk</div>
        </div>
        <div className="risk-stat risk-medium">
          <div className="stat-value">{medium_risk_count || 0}</div>
          <div className="stat-label">Medium Risk</div>
        </div>
        <div className="risk-stat risk-low">
          <div className="stat-value">{low_risk_count || 0}</div>
          <div className="stat-label">Low Risk</div>
        </div>
      </div>

      {requires_review && (
        <div className="risk-alert">
          <strong>‚ö†Ô∏è Action Required:</strong> Please review flagged transactions before submitting to SARS
        </div>
      )}

      {auditRisk.risk_categories && Object.keys(auditRisk.risk_categories).length > 0 && (
        <div className="risk-categories">
          <h4>Issue Breakdown</h4>
          <ul>
            {Object.entries(auditRisk.risk_categories).map(([category, count]) => (
              <li key={category}>
                <span className="category-name">{category.replace(/_/g, ' ')}</span>
                <span className="category-count">{count}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default RiskSummary;
