import React, { useState } from 'react';
import './FlaggedTransactions.css';

/**
 * FlaggedTransactions Component
 * Displays all flagged transactions with their risk details
 */
const FlaggedTransactions = ({ flags, transactions }) => {
  const [expandedFlag, setExpandedFlag] = useState(null);
  const [severityFilter, setSeverityFilter] = useState('all');

  if (!flags || flags.length === 0) {
    return (
      <div className="flagged-transactions">
        <div className="no-flags">
          <span className="success-icon">âœ…</span>
          <h3>No Issues Detected</h3>
          <p>All transactions passed validation checks</p>
        </div>
      </div>
    );
  }

  // Filter flags by severity
  const filteredFlags = severityFilter === 'all'
    ? flags
    : flags.filter(flag => flag.severity === severityFilter);

  const getSeverityIcon = (severity) => {
    switch (severity) {
      case 'high':
        return 'ðŸš¨';
      case 'medium':
        return 'âš ï¸';
      case 'low':
        return 'â„¹ï¸';
      default:
        return 'â€¢';
    }
  };

  const getSeverityClass = (severity) => {
    return `flag-severity-${severity}`;
  };

  const toggleFlag = (index) => {
    setExpandedFlag(expandedFlag === index ? null : index);
  };

  return (
    <div className="flagged-transactions">
      <div className="flags-header">
        <h3>ðŸš© Flagged Transactions</h3>
        <div className="flags-filter">
          <button
            className={severityFilter === 'all' ? 'active' : ''}
            onClick={() => setSeverityFilter('all')}
          >
            All ({flags.length})
          </button>
          <button
            className={severityFilter === 'high' ? 'active severity-high' : ''}
            onClick={() => setSeverityFilter('high')}
          >
            High ({flags.filter(f => f.severity === 'high').length})
          </button>
          <button
            className={severityFilter === 'medium' ? 'active severity-medium' : ''}
            onClick={() => setSeverityFilter('medium')}
          >
            Medium ({flags.filter(f => f.severity === 'medium').length})
          </button>
          <button
            className={severityFilter === 'low' ? 'active severity-low' : ''}
            onClick={() => setSeverityFilter('low')}
          >
            Low ({flags.filter(f => f.severity === 'low').length})
          </button>
        </div>
      </div>

      <div className="flags-list">
        {filteredFlags.map((flag, index) => (
          <div
            key={index}
            className={`flag-item ${getSeverityClass(flag.severity)} ${expandedFlag === index ? 'expanded' : ''}`}
          >
            <div className="flag-summary" onClick={() => toggleFlag(index)}>
              <div className="flag-icon">{getSeverityIcon(flag.severity)}</div>
              <div className="flag-content">
                <div className="flag-title">
                  <span className="flag-tx-number">Transaction #{flag.transaction_number}</span>
                  <span className={`flag-severity-badge ${flag.severity}`}>
                    {flag.severity.toUpperCase()}
                  </span>
                </div>
                <div className="flag-message">{flag.message}</div>
              </div>
              <div className="flag-toggle">
                {expandedFlag === index ? 'â–¼' : 'â–¶'}
              </div>
            </div>

            {expandedFlag === index && (
              <div className="flag-details">
                <div className="flag-detail-section">
                  <strong>Issue Type:</strong>
                  <span className="flag-type">{flag.rule_type.replace(/_/g, ' ')}</span>
                </div>

                <div className="flag-detail-section">
                  <strong>Recommendation:</strong>
                  <p className="flag-recommendation">{flag.recommendation}</p>
                </div>

                {flag.metadata && Object.keys(flag.metadata).length > 0 && (
                  <div className="flag-detail-section">
                    <strong>Additional Details:</strong>
                    <ul className="flag-metadata">
                      {Object.entries(flag.metadata).map(([key, value]) => (
                        <li key={key}>
                          <span className="meta-key">{key}:</span>
                          <span className="meta-value">{JSON.stringify(value)}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {transactions && transactions[flag.transaction_index] && (
                  <div className="flag-detail-section">
                    <strong>Transaction Details:</strong>
                    <div className="transaction-preview">
                      <div className="tx-detail">
                        <span className="tx-label">Date:</span>
                        <span className="tx-value">{transactions[flag.transaction_index].date}</span>
                      </div>
                      <div className="tx-detail">
                        <span className="tx-label">Type:</span>
                        <span className="tx-value">{transactions[flag.transaction_index].type}</span>
                      </div>
                      <div className="tx-detail">
                        <span className="tx-label">Asset:</span>
                        <span className="tx-value">{transactions[flag.transaction_index].asset}</span>
                      </div>
                      <div className="tx-detail">
                        <span className="tx-label">Amount:</span>
                        <span className="tx-value">{transactions[flag.transaction_index].amount}</span>
                      </div>
                    </div>
                  </div>
                )}

                <div className="flag-timestamp">
                  Detected: {flag.timestamp}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {filteredFlags.length === 0 && (
        <div className="no-filtered-flags">
          <p>No {severityFilter} risk flags found</p>
        </div>
      )}
    </div>
  );
};

export default FlaggedTransactions;
