import React, { useState } from 'react';
import './AuditRiskSummary.css';

const AuditRiskSummary = ({ auditRiskSummary, detectionResults }) => {
  const [expandedSection, setExpandedSection] = useState(null);

  if (!auditRiskSummary && !detectionResults) {
    return null;
  }

  const riskLevel = auditRiskSummary?.riskLevel || 'LOW';
  const totalIssues = auditRiskSummary?.totalIssues || 0;
  const breakdown = auditRiskSummary?.breakdown || { high: 0, medium: 0, low: 0 };
  const transfersDetected = auditRiskSummary?.transfersDetected || 0;
  const redFlagsDetected = auditRiskSummary?.redFlagsDetected || 0;
  const recommendations = auditRiskSummary?.recommendations || [];

  const redFlags = detectionResults?.redFlags || [];
  const transfers = detectionResults?.transfers || [];

  const getRiskLevelClass = (level) => {
    switch (level) {
      case 'HIGH':
        return 'risk-level-high';
      case 'MEDIUM':
        return 'risk-level-medium';
      case 'LOW':
        return 'risk-level-low';
      default:
        return 'risk-level-low';
    }
  };

  const getRiskLevelIcon = (level) => {
    switch (level) {
      case 'HIGH':
        return 'âš ï¸';
      case 'MEDIUM':
        return 'âš¡';
      case 'LOW':
        return 'âœ…';
      default:
        return 'âœ…';
    }
  };

  const toggleSection = (section) => {
    setExpandedSection(expandedSection === section ? null : section);
  };

  return (
    <div className="audit-risk-summary">
      <div className="risk-header">
        <h2>
          <span className="risk-icon">{getRiskLevelIcon(riskLevel)}</span>
          Audit Risk Summary
        </h2>
        <div className={`risk-badge ${getRiskLevelClass(riskLevel)}`}>
          {riskLevel} RISK
        </div>
      </div>

      <div className="risk-overview">
        <div className="risk-stats">
          <div className="risk-stat-card">
            <div className="stat-value">{totalIssues}</div>
            <div className="stat-label">Total Issues</div>
          </div>
          <div className="risk-stat-card high">
            <div className="stat-value">{breakdown.high}</div>
            <div className="stat-label">High Priority</div>
          </div>
          <div className="risk-stat-card medium">
            <div className="stat-value">{breakdown.medium}</div>
            <div className="stat-label">Medium Priority</div>
          </div>
          <div className="risk-stat-card low">
            <div className="stat-value">{breakdown.low}</div>
            <div className="stat-label">Low Priority</div>
          </div>
        </div>

        <div className="detection-stats">
          <div className="detection-card transfers">
            <div className="detection-icon">ðŸ”„</div>
            <div className="detection-info">
              <div className="detection-value">{transfersDetected}</div>
              <div className="detection-label">Non-Taxable Transfers</div>
            </div>
          </div>
          <div className="detection-card red-flags">
            <div className="detection-icon">ðŸš©</div>
            <div className="detection-info">
              <div className="detection-value">{redFlagsDetected}</div>
              <div className="detection-label">Red Flags Detected</div>
            </div>
          </div>
        </div>
      </div>

      {recommendations && recommendations.length > 0 && (
        <div className="recommendations-section">
          <h3>ðŸ“‹ Recommendations</h3>
          <ul className="recommendations-list">
            {recommendations.map((rec, index) => (
              <li key={index} className="recommendation-item">
                {rec}
              </li>
            ))}
          </ul>
        </div>
      )}

      {redFlags && redFlags.length > 0 && (
        <div className="detail-section">
          <div 
            className="section-header"
            onClick={() => toggleSection('redFlags')}
          >
            <h3>ðŸš© Red Flags Details ({redFlags.length})</h3>
            <span className="toggle-icon">
              {expandedSection === 'redFlags' ? 'â–¼' : 'â–¶'}
            </span>
          </div>
          
          {expandedSection === 'redFlags' && (
            <div className="section-content">
              <table className="detail-table">
                <thead>
                  <tr>
                    <th>Line</th>
                    <th>Date</th>
                    <th>Type</th>
                    <th>Severity</th>
                    <th>Category</th>
                    <th>Reason</th>
                  </tr>
                </thead>
                <tbody>
                  {redFlags.map((flag, index) => (
                    <tr key={index} className={`severity-${flag.severity?.toLowerCase()}`}>
                      <td>{flag.lineNumber}</td>
                      <td>{new Date(flag.date).toLocaleDateString('en-ZA')}</td>
                      <td>{flag.type}</td>
                      <td>
                        <span className={`severity-badge ${flag.severity?.toLowerCase()}`}>
                          {flag.severity}
                        </span>
                      </td>
                      <td>{flag.category}</td>
                      <td className="reason-cell">{flag.reason}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {transfers && transfers.length > 0 && (
        <div className="detail-section">
          <div 
            className="section-header"
            onClick={() => toggleSection('transfers')}
          >
            <h3>ðŸ”„ Non-Taxable Transfers ({transfers.length})</h3>
            <span className="toggle-icon">
              {expandedSection === 'transfers' ? 'â–¼' : 'â–¶'}
            </span>
          </div>
          
          {expandedSection === 'transfers' && (
            <div className="section-content">
              <table className="detail-table">
                <thead>
                  <tr>
                    <th>Line</th>
                    <th>Date</th>
                    <th>Type</th>
                    <th>From</th>
                    <th>Amount</th>
                    <th>To</th>
                    <th>Classification</th>
                  </tr>
                </thead>
                <tbody>
                  {transfers.map((transfer, index) => (
                    <tr key={index} className="transfer-row">
                      <td>{transfer.lineNumber}</td>
                      <td>{new Date(transfer.date).toLocaleDateString('en-ZA')}</td>
                      <td>{transfer.type}</td>
                      <td>{transfer.fromCurrency}</td>
                      <td>{transfer.fromAmount?.toFixed(8)}</td>
                      <td>{transfer.toCurrency}</td>
                      <td>
                        <span className="transfer-classification">
                          {transfer.transferInfo?.classification || 'Transfer'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AuditRiskSummary;
