import React, { useState } from 'react';
import './AuditRiskReport.css';

const AuditRiskReport = ({ auditRiskReport, suspiciousTransactions }) => {
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [showAllFlags, setShowAllFlags] = useState(false);

  if (!auditRiskReport || !suspiciousTransactions) {
    return null;
  }

  const { summary, overallRiskLevel, flaggedTransactions, recommendations } = auditRiskReport;

  const getRiskColor = (level) => {
    switch (level) {
      case 'HIGH':
        return '#ef4444';
      case 'MEDIUM-HIGH':
        return '#f97316';
      case 'MEDIUM':
        return '#f59e0b';
      case 'LOW':
        return '#10b981';
      default:
        return '#6b7280';
    }
  };

  const getRiskBadgeClass = (level) => {
    switch (level) {
      case 'HIGH':
        return 'risk-badge-high';
      case 'MEDIUM':
        return 'risk-badge-medium';
      case 'LOW':
        return 'risk-badge-low';
      default:
        return 'risk-badge-default';
    }
  };

  const getCategoryIcon = (category) => {
    const icons = {
      'MISSING_DATA': '‚ö†Ô∏è',
      'NEGATIVE_BUY_AMOUNT': 'üî¥',
      'NEGATIVE_SELL_AMOUNT': 'üî¥',
      'ZERO_PRICE': '‚ö°',
      'POSSIBLE_MISCLASSIFIED_TRANSFER': 'üîÑ',
      'CRYPTO_TO_CRYPTO_SELL': 'üîÄ',
      'TRANSFER_WITH_FIAT': 'üí±',
      'LARGE_TRANSACTION': 'üí∞',
      'DUPLICATE_TRANSACTION': 'üìã',
      'WASH_TRADING_PATTERN': 'üîÅ',
      'INSUFFICIENT_HOLDINGS': '‚ùå',
      'LARGE_CAPITAL_LOSS': 'üìâ'
    };
    return icons[category] || '‚ö†Ô∏è';
  };

  const displayedFlags = showAllFlags ? flaggedTransactions : flaggedTransactions.slice(0, 5);

  return (
    <div className="audit-risk-report">
      <div className="report-header">
        <h2>üõ°Ô∏è Audit Risk Report</h2>
        <div className="overall-risk" style={{ borderColor: getRiskColor(overallRiskLevel) }}>
          <span className="risk-label">Overall Risk Level:</span>
          <span className={`risk-badge ${getRiskBadgeClass(overallRiskLevel)}`}>
            {overallRiskLevel}
          </span>
        </div>
      </div>

      {summary.totalFlagged === 0 ? (
        <div className="no-issues">
          <div className="success-icon">‚úÖ</div>
          <h3>No Issues Detected</h3>
          <p>Your transaction data appears clean and ready for tax submission.</p>
        </div>
      ) : (
        <>
          <div className="risk-summary">
            <div className="summary-cards">
              <div className="summary-card">
                <div className="card-value">{summary.totalFlagged}</div>
                <div className="card-label">Total Flagged</div>
              </div>
              <div className="summary-card high">
                <div className="card-value">{summary.highRisk}</div>
                <div className="card-label">High Risk</div>
              </div>
              <div className="summary-card medium">
                <div className="card-value">{summary.mediumRisk}</div>
                <div className="card-label">Medium Risk</div>
              </div>
              <div className="summary-card low">
                <div className="card-value">{summary.lowRisk}</div>
                <div className="card-label">Low Risk</div>
              </div>
            </div>

            <div className="categories-breakdown">
              <h3>Issues by Category</h3>
              <div className="categories-grid">
                {Object.entries(summary.categories).map(([category, count]) => (
                  <div 
                    key={category} 
                    className="category-item"
                    onClick={() => setExpandedCategory(expandedCategory === category ? null : category)}
                  >
                    <span className="category-icon">{getCategoryIcon(category)}</span>
                    <span className="category-name">
                      {category.replace(/_/g, ' ')}
                    </span>
                    <span className="category-count">{count}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="recommendations-section">
            <h3>üìã Recommendations</h3>
            <div className="recommendations-list">
              {recommendations.map((rec, index) => (
                <div key={index} className={`recommendation ${rec.priority.toLowerCase()}`}>
                  <span className="priority-badge">{rec.priority}</span>
                  <p>{rec.message}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="flagged-transactions-section">
            <h3>üö® Flagged Transactions</h3>
            <div className="flagged-transactions-list">
              {displayedFlags.map((flag, index) => (
                <div key={index} className="flagged-item">
                  <div className="flag-header">
                    <div className="flag-info">
                      <span className="flag-icon">{getCategoryIcon(flag.category)}</span>
                      <span className="flag-date">{new Date(flag.date).toLocaleDateString()}</span>
                      <span className="flag-type">{flag.type}</span>
                      <span className="flag-line">Line {flag.lineNumber}</span>
                    </div>
                    <span className={`risk-badge ${getRiskBadgeClass(flag.riskLevel)}`}>
                      {flag.riskLevel}
                    </span>
                  </div>
                  <div className="flag-body">
                    <div className="flag-category">{flag.category.replace(/_/g, ' ')}</div>
                    <div className="flag-description">{flag.description}</div>
                    <div className="flag-recommendation">
                      <strong>üí° Recommendation:</strong> {flag.recommendation}
                    </div>
                    <div className="flag-transaction-details">
                      <span>{flag.transaction.fromAmount.toFixed(8)} {flag.transaction.fromCurrency}</span>
                      <span>‚Üí</span>
                      <span>{flag.transaction.toAmount.toFixed(8)} {flag.transaction.toCurrency}</span>
                      {flag.transaction.wallet && (
                        <span className="wallet-badge">{flag.transaction.wallet}</span>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {flaggedTransactions.length > 5 && (
              <button 
                className="show-more-button"
                onClick={() => setShowAllFlags(!showAllFlags)}
              >
                {showAllFlags ? 'Show Less' : `Show All (${flaggedTransactions.length - 5} more)`}
              </button>
            )}
          </div>

          <div className="detection-stats">
            <p>
              <strong>Detection Rate:</strong> {suspiciousTransactions.flaggedPercentage}% of transactions flagged
            </p>
            <p className="report-timestamp">
              Report generated: {new Date(auditRiskReport.generatedAt).toLocaleString()}
            </p>
          </div>
        </>
      )}
    </div>
  );
};

export default AuditRiskReport;
